sys_script_include.script:
var WhenTaskCIChange = Class.create();
WhenTaskCIChange.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	CountSameClass: function(){
		var ciRecord = new GlideRecord("cmdb_ci");
		if (ciRecord.get(this.getParameter('sysparm_ciID'))) {
			ciRecord.get(this.getParameter('sysparm_ciID'));
			var classCi = ciRecord.sys_class_name;
			//gs.info(" ALPHONSE => "+classCi);
			var ci = new GlideAggregate("cmdb_ci");
				ci.addQuery('sys_class_name',classCi);
				ci.addAggregate('COUNT');
				ci.query();
				var classes = 0;
				if(ci.next()) 
					classes = ci.getAggregate('COUNT');
			//gs.info(" ALPHONSE n=> "+classes);

		return classes;
      }
	},
    type: 'WhenTaskCIChange'
});
